impl Solution {
    pub fn minmax_gas_dist(stations: Vec<i32>, k: i32) -> f64 {
        let N = stations.len() - 1;
        let mut deltas: Vec<f64> = Vec::with_capacity(N - 1);
        (0..N).for_each(|index| {
            deltas.push((stations[index + 1] - stations[index]) as f64);
        });
        let mut dp: Vec<Vec<f64>> = vec![vec![0f64; k as usize + 1]; N];
        (0..=k).for_each(|index| {
            dp[0][index as usize] = deltas[0] as f64 / (index as f64 + 1f64);
        });
        
        // println!("{:?}", deltas);
        // println!("{:?}", dp);
        
        (1..N).for_each(|p| {
            (0..=k).for_each(|q| {
                let mut bns = 999_999_999f64;
                (0..=q).for_each(|x| {
                    bns = f64::min(bns, f64::max(deltas[p] / (x as f64 + 1f64), dp[p - 1][(q - x) as usize]));
                    dp[p][q as usize] = bns
                });
            });
        });

        // println!("{:?}", dp);

        dp[N - 1][k as usize]
    }
}